const allItems = $input.all().map((item) => item.json);

let telegramItems = [];
try {
  telegramItems = $("Media_queue Trigger").all().map((item) => item.json);
} catch (e) {
  telegramItems = [];
}

// Capturamos el caption (solo una vez, del primer elemento disponible)
const caption = telegramItems[0]?.payload?.captions || "";

// Creamos la lista de archivos usando file_description
const fileList = allItems.map((item, index) => {
  const fileDesc = item.file_description || "";
  return `file${index + 1}: ${fileDesc}`;
});

// Unificamos el mensaje
const unifiedMessage = `caption: ${caption}\n${fileList.join("\n")}`;

return {
  json: {
    unifiedMessage
  }
};

const results = [];
for (const item of $input.all()) {
  const fileName = item.json?.fileName || item.json?.message?.document?.file_name || '';
  const ext = fileName.toLowerCase().split('.').pop();

  let type = 'fallback';
  if (['csv'].includes(ext)) type = 'csv';
  else if (['html', 'htm'].includes(ext)) type = 'html';
  else if (['ics'].includes(ext)) type = 'ics';
  else if (['json'].includes(ext)) type = 'json';
  else if (['ods'].includes(ext)) type = 'ods';
  else if (['pdf'].includes(ext)) type = 'pdf';
  else if (['rtf'].includes(ext)) type = 'rtf';
  else if (['txt', 'md', 'log'].includes(ext)) type = 'text file';
  else if (['xml'].includes(ext)) type = 'xml';
  else if (['xls', 'xlsx'].includes(ext)) type = 'spreadsheet';
  else if (['jpg', 'jpeg', 'png', 'webp', 'gif', 'bmp', 'tiff'].includes(ext)) type = 'image';
  else if (['mp3', 'wav', 'ogg', 'm4a', 'flac'].includes(ext)) type = 'audio';
  else if (['mp4', 'mov', 'avi', 'mkv', 'webm'].includes(ext)) type = 'video';

  item.json.fileTypeCategory = type;
  results.push(item);
}
return results;

