const inputItems = $input.all();
const out = [];

for (const item of inputItems) {
  const j = item.json || {};
  const raw =
    j.message ?? j.output ?? j.text ?? j.content ?? '';

  const formatted = processMarkdownV2Safe(raw);
  const chunks = chunkForTelegram(formatted, SAFE_BUDGET);

  chunks.forEach((chunk, idx) => {
    out.push({
      json: {
        ...j,
        message: chunk,
        message_part_index: idx + 1,
        message_parts_total: chunks.length,
      },
      binary: item.binary,
    });
  });
}

return out;
